	/**
	 * Método privado que devuelve un objeto BSContexto.
	 * 
	 * @return BSContexto
	 */
	private BSContexto getContexto() {
		final BSContexto contexto = bsContextoService.getContexto();
		if (contexto == null) {
			ConfigurableNavigationHandler configurableNavigationHandler = (ConfigurableNavigationHandler) FacesContext
					.getCurrentInstance().getApplication()
					.getNavigationHandler();
			final String ruta = "/views/login/login?faces-redirect=true";
			configurableNavigationHandler.performNavigation(ruta);
			throw new NoControlableException("Contexto no disponible", this
					.getClass().getName() + ": Contexto no disponible");
		}
		return contexto;
	}

	/**
	 * Método privado que devuelve un objeto UsuarioBean.
	 * 
	 * @return UsuarioBean
	 */
	private UsuarioBean getUsuarioBean() {
		final UsuarioBean usuarioBean = this.getContexto().getUsuario();
		if (usuarioBean == null) {
			throw new NoControlableException(
					"Datos de usuario no disponibles en el contexto",
					this.getClass().getName()
							+ ": Datos de usuario no disponibles en el contexto");
		}
		return this.getContexto().getUsuario();
	}

	/**
	 * Método que devuelve un indicador booleano para determinar si existe el
	 * contexto.
	 * 
	 * @return indicador booleano
	 */
	public boolean existeContexto() {
		return (bsContextoService != null && bsContextoService.getContexto() != null);
	}

