	/**
	 * Método que permite guardar un mensaje de usuario.
	 */
	public void guardaMensaje() {
		JSONObject msgJson;
		try {
			final NotificacionBean msg = new NotificacionBean();
			msgJson = new JSONObject("{"
					+ new JSONObject(getMensajeJSON()).getString("content")
					+ "}");
			msg.setClave(new UUIDGeneradorUtils().generarIDNumerico());
			msg.setDestinatario((String) msgJson.get("destinatario"));
			msg.setIndUrgencia((String) msgJson.get("indUrgencia"));
			msg.setTexto((String) msgJson.get("texto"));
			DateFormat formatter = new SimpleDateFormat("yyyyMMdd");
			msg.setFechaVigenteDesde(formatter.parse(msgJson
					.getString("fechaVigenteDesde")));
			msg.setFechaVigenteHasta(formatter.parse(msgJson
					.getString("fechaVigenteHasta")));
			msg.setFechaCreacion(formatter.parse(msgJson
					.getString("fechaCreacion")));
			getNotificacionesUsuario().add(msg);
			final FacesMessage fcsmsg = new FacesMessage();
			fcsmsg.setSummary(msg.getDestinatario());
			fcsmsg.setDetail(msg.getTexto());
			FacesContext.getCurrentInstance().addMessage("sysMsgs", fcsmsg);
		} catch (final JSONException e) {
			// throw new NoControlableException(
			// "Error al ejecutar el método para publicar mensajes", e);
		} catch (ParseException e) {
			// throw new NoControlableException(
			// "Error al ejecutar el método para publicar mensajes", e);
		}
	}

