	/**
	 * Método que devuelve un objeto del mapa de configuraciones.
	 * 
	 * @return null si no lo encuentra o el objeto si existe
	 */
	public Object getConfigData(final String paramKey) {
		final Map<String, Object> configuracion = this.getContexto()
				.getConfiguracion();
		if ((configuracion == null) || (!configuracion.containsKey(paramKey))) {
			return null;
		}
		return configuracion.get(paramKey);
	}

	/**
	 * Funcion que verifica que se tiene la información del usaurio fundamental
	 * para realizar consultas en servicio web
	 * 
	 * @return
	 */
	public boolean isContextoCompleto() {
		if (existeContexto()
				&& this.getUsuarioBean() != null
				&& StringUtils.isNotBlank(this.getUsuarioBean().getId())
				&& StringUtils.isNotBlank(this.getUsuarioBean().getPwd())
				&& StringUtils.isNotBlank(this.getUsuarioBean().getTerminal())
				&& StringUtils.isNotBlank(this.getUsuarioBean()
						.getDireccionIp())) {
			return true;
		} else {
			return false;
		}
	}

